import Sketch from "react-p5";
import { Container } from 'react-bootstrap';

const digits ="";

const colors = [ "#641E16" , "#512E5F" , "#154360" , "#0E6251", "#145A32", "#7E5109", "#6E2C00", "#626567", "#1B2631", "#CACFD2" ];

const PhiArt = () => {

  const setup = (p5, canvasParentRef) => {
    p5.createCanvas(800, 800).parent(canvasParentRef);
    p5.background("#34495E");
    p5.noLoop();
  };

  const draw = p5 => {
    const digitsArray = digits.split('');
    for( let i = 0 ; i < digitsArray.length ; i ++ ) {
      p5.fill(colors[digitsArray[i]]);
      p5.ellipse(p5.random(1,800), p5.random(1,800), p5.random(1,25));
    }
  }

  return (
    <Container>
      <div className='starter-template text-center mt-5'>
        <Sketch setup={setup} draw={draw} />
      </div>
    </Container>
  )
}

export default PhiArt
